import React from 'react'
import {Table,Card,Select,Input,Button,Icon} from "antd"
import LinkButton from "../../components/link-button";
import {reqProducts,reqSearchProducts} from "../../api";
import {PAGE_SIZE} from "../../utils/constant";

const Option = Select.Option;

export default class ProductHome extends React.Component {

    state = {
        products:[],
        total:0,
        loading:false,
        searchName:'',
        searchType:'productName'

    }

    initColumns = () =>{
        this.columns = [
            {
                title: '商品名称',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '商品描述',
                dataIndex: 'desc',
                key: 'age',
            },
            {
                title: '价格',
                dataIndex: 'price',
                render: (price)=>"￥"+price,
            }, {
                width:100,
                title: '状态',
                dataIndex: 'status',
                render: (status)=>{
                    return (
                        <span>
                            <Button type="primary" >下架</Button>
                        <span>在售</span>
                    </span>)
                },
            },{
                width:100,
                title: '操作',
                render: (product)=>{
                    return (
                        <span>
                            <LinkButton onClick={()=>this.props.history.push("/product/detail",product)}>详情</LinkButton>
                            <LinkButton>修改</LinkButton>
                    </span>)
                },
            },
        ];
    }

    getProducts = async(pageNum) =>{
        this.setState({loading:true})
        const {searchName,searchType} = this.state
        let result;
        if(searchName){
             result = await  reqSearchProducts({pageNum,pageSize:PAGE_SIZE,searchName,searchType})
        }else{
            result = await reqProducts(pageNum,PAGE_SIZE);
        }

        this.setState({loading:false})
        console.log(result.data);
        if(result.data.status === 0){
            const {total,list} = result.data.data;
            this.setState({
                total,
                products:list
            })
        }
    }

    componentWillMount() {
        this.initColumns()
    }

    componentDidMount() {
        this.getProducts(1)
    }

    render(){
        const {products,total,loading,searchName,searchType} = this.state;

        const title = (
            <span>
                <Select value={searchType} style={{width:150}} onChange={value=>this.setState({searchType: value})}>
                    <Option value="productName">按名称搜索</Option>
                    <Option value="productDesc">按描述搜索</Option>
                </Select>
                <Input placeholder="关键字"
                       style={{width:150,margin:'0 15px'}}
                       value={searchName}
                       onChange={event=>this.setState({searchName:event.target.value})} />
                <Button type="primary" onClick={()=>this.getProducts(1)}>搜索</Button>
            </span>);

        const extra = (<Button type="primary">
            <Icon type="plus"></Icon>
            添加商品
        </Button>);





        return (
            <Card title={title} extra={extra}>
                <Table
                    bordered
                    rowKey="_id"
                    loadding={loading}
                    dataSource={products}
                    pagination={
                        {
                            total:total,
                            defaultPageSize:PAGE_SIZE,
                            onChange:this.getProducts
                        }
                    }
                    columns={this.columns} />;
            </Card>
        )
    }
}
