import React from 'react'
import {Table,Card,Select,Input,Button,Icon} from "antd"
import LinkButton from "../../components/link-button";

const Option = Select.Option;

export default class ProductHome extends React.Component {

    state = {
        products:[{
            "status":1,
            "imgs":[
                "image-155.jpg",
                "image-156.jpg"
            ],
            "_id":"5ca9c",
            "name":"联想ThinkPad",
            "desc":"年度重磅新品",
            "price":66500,
            "pCategoryId":"5ca9d6c",
            "categoryId":"5ca9db7",
            "detail":"<p><span style='color:rgb(228,57,60);background-color: rgb(28,140,33)'></span></p>",
            "_v":0
        }],

    }

    initColumns = () =>{
        this.columns = [
            {
                title: '商品名称',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '商品描述',
                dataIndex: 'desc',
                key: 'age',
            },
            {
                title: '价格',
                dataIndex: 'price',
                render: (price)=>"￥"+price,
            }, {
                width:100,
                title: '状态',
                dataIndex: 'status',
                render: (status)=>{
                    return (
                        <span>
                            <Button type="primary" >下架</Button>
                        <span>在售</span>
                    </span>)
                },
            },{
                width:100,
                title: '操作',
                render: (product)=>{
                    return (
                        <span>
                            <LinkButton>详情</LinkButton>
                            <LinkButton>修改</LinkButton>
                    </span>)
                },
            },
        ];
    }

    componentWillMount() {
        this.initColumns()
    }

    render(){
        const {products} = this.state;

        const title = (
            <span>
                <Select value="1" style={{width:150}}>
                    <Option value="0">按名称搜索</Option>
                    <Option value="1">按描述搜索</Option>
                </Select>
                <Input placeholder="关键字" style={{width:150,margin:'0 15px'}}/>
                <Button type="primary">搜索</Button>
            </span>);

        const extra = (<Button type="primary">
            <Icon type="plus"></Icon>
            添加商品
        </Button>);





        return (
            <Card title={title} extra={extra}>
                <Table
                    bordered
                    rowKey="_id"
                    dataSource={products}
                    columns={this.columns} />;
            </Card>
        )
    }
}
