import React from 'react'
import {Button,Table,Card} from "antd"
import {PAGE_SIZE} from "../../utils/constant";

export default class Role extends React.Component {
    state = {
        roles:[{
            "menus":[
                "/home",
                "/products",
                "/category",
                "/product",
                "/role"
            ],
            "_id":"5ca9eac0",
            "name":"角色1",
            "create_time":1554639552758,
            "_v":0,
            "auth_time":1557630307021,
            "auth_name":"admim"
        }],
        loading:false,
        role:{}
    }

    initColumn = () =>{
        this.columns = [
            {
                title:'角色名称',
                dataIndex:'name'
            }, {
                title:'创建时间',
                dataIndex:'create_time'
            },{
                title:'授权时间',
                dataIndex:'auth_time'
            },{
                title:'授权人',
                dataIndex:'auth_name'
            },
        ]
    }

    componentWillMount(){
        this.initColumn()
    }

    onRow = (role)=>{
        return {
            onclick : event=>{
                this.setState({
                    role
                })
            }
        }
    }

    render(){
        const {roles,loading,role} = this.state

        const title=(
            <span>
                    <Button type="primary">创建角色</Button>&nbsp;&nbsp;
                    <Button type="primary" disabled>设置角色权限</Button>
                </span>
        )

        const rowSelection = ({
            type:'radio',
            selectedKeys:[role._id]
        })



        return (
            <Card title={title} >
                <Table
                    bordered
                    rowKey="_id"
                    loadding={loading}
                    dataSource={roles}
                    columns={this.columns}
                    pagination={{defaultPageSize:5}}
                    rowSelection={rowSelection}
                    onRow={this.onRow}
                 />
            </Card>
        )
    }
}
