import React from 'react'
import "./index.less"
import logo from "../../assets/images/logo.png"
import {Link} from "react-router-dom"
import { Menu, Icon, Button } from 'antd';
const { SubMenu } = Menu;

export default class LeftNav extends React.Component{
    state = {
        collapsed: false,
    };

    toggleCollapsed = () => {
        this.setState({
            collapsed: !this.state.collapsed,
        });
    };

    render() {
        return (

            <div   className="left-nav">
                <Link to="/" className="left-nav-header">
                    <img src={logo} />
                    <h1>React后台</h1>
                </Link>
                <Menu
                    defaultSelectedKeys={['1']}
                    defaultOpenKeys={['sub1']}
                    mode="inline"
                    theme="dark"
                >
                    <Menu.Item key="/home">
                            <Link to="/home">
                                <Icon type="pie-chart" />
                                <span>首页</span>
                            </Link>
                    </Menu.Item>
                    <SubMenu
                        key="sub1"
                        title={
                            <span>
                                <Icon type="mail" />
                                <span>商品</span>
                            </span>
                            }
                        >
                        <Menu.Item key="/category">
                            <Link to="/category">
                                <Icon type="mail" />
                                <span>品类管理</span>
                            </Link>
                        </Menu.Item>
                        <Menu.Item key="/product">
                            <Link to="/product">
                                <Icon type="mail" />
                                <span>商品管理</span>
                            </Link>
                        </Menu.Item>
                    </SubMenu>

                </Menu>
            </div>
        )
    }
}
