import React from 'react'
import {Button, Table, Card, Modal,message} from "antd"
import {PAGE_SIZE} from "../../utils/constant";
import AddForm from "./add-form";
import {reqAddUser} from "../../api";

export default class Role extends React.Component {
    state = {
        roles:[{
            "menus":[
                "/home",
                "/products",
                "/category",
                "/product",
                "/role"
            ],
            "_id":"5ca9eac0",
            "name":"角色1",
            "create_time":1554639552758,
            "_v":0,
            "auth_time":1557630307021,
            "auth_name":"admim"
        }],
        loading:false,
        role:{},
        isShowAdd:false
    }

    initColumn = () =>{
        this.columns = [
            {
                title:'角色名称',
                dataIndex:'name'
            }, {
                title:'创建时间',
                dataIndex:'create_time'
            },{
                title:'授权时间',
                dataIndex:'auth_time'
            },{
                title:'授权人',
                dataIndex:'auth_name'
            },
        ]
    }

    addRole =() =>{
        this.form.validateFields(async(err,values)=>{
            if(!err){
                const {roleName} = values
                this.form.resetFields()
                const result = await reqAddUser(roleName)
                if(result.status===0){
                    message.success("添加陈宫")
                    const role = result.data
                    const roles  = this.state.roles;
                    roles.push(role)
                    this.setState({roles})

                }else{
                    message.error("添加失败")
                }
            }
        })
    }

    handleCancel =()=>{
        this.setState({isShowAdd:false})
    }

    componentWillMount(){
        this.initColumn()
    }

    onRow = (role)=>{
        return {
            onClick : event=>{
                this.setState({
                    role
                })
            }
        }
    }

    render(){
        const {roles,loading,role,isShowAdd} = this.state

        const title=(
            <span>
                    <Button type="primary" onClick={()=>this.setState({isShowAdd: true})}>创建角色</Button>&nbsp;&nbsp;
                    <Button type="primary" disabled={!role._id}>设置角色权限</Button>
            </span>
        )

        const rowSelection = ({
            type:'radio',
            selectedRowKeys:[role._id]
        })



        return (
            <Card title={title} >
                <Table
                    bordered
                    rowKey="_id"
                    loadding={loading}
                    dataSource={roles}
                    columns={this.columns}
                    pagination={{defaultPageSize:5}}
                    rowSelection={rowSelection}
                    onRow={this.onRow}
                 />
                <Modal
                    title="添加角色"
                    visible={isShowAdd}
                    onOk={this.addRole}
                    onCancel={this.handleCancel}
                >
                    <AddForm  setForm={(form)=> {this.form = form} } />
                </Modal>
            </Card>
        )
    }
}
