import React from 'react'
import {Card,Form,Input,Cascader,Upload,Button,Icon} from "antd"
import LinkButton from "../../components/link-button";

const {Item} = Form
const {TextArea} = Input

const options = [
    {
        value: 'zhejiang',
        label: 'Zhejiang',
        isLeaf: false,
    },
    {
        value: 'jiangsu',
        label: 'Jiangsu',
        isLeaf: false,
    },
];

class ProductAddUpdate extends React.Component {

    state = {
        options,
    };

    submit = () =>{
        this.props.form.validateFields((err,values)=>{
            if(!err){
                console.log("ok")
            }
        })
    }

    validattorPrice = (rule,value,callback) =>{
        if(value*1>0){
            callback()
        }else{
            callback("价格格式不对")
        }

    }

    render(){
        const {getFieldDecorator } = this.props.form

        const title = (
            <span>
                <LinkButton>
                    <Icon type="arrow-left" style={{fontSize:20}}/>
                </LinkButton>
                <span>添加商品</span>
            </span>
        )

        const formItemLayout = {
            labelCol: { span: 2 },
            wrapperCol: { span: 8},
        };


        return (
            <Card title={title}>
                <Form {...formItemLayout}>
                    <Item label="商品名称:">
                        {
                            getFieldDecorator('name',{
                                initialValue:'',
                                rules:[
                                    {required:true,message:"请输入商品名称"}
                                ]
                            })(
                                <Input placeholder="商品名称"></Input>
                            )
                        }
                    </Item>
                    <Item label="商品描述:">
                        {
                            getFieldDecorator('desc',{
                                initialValue:'',
                                rules:[
                                    {required:true,message:"请输入商品描述"}
                                ]
                            })(
                                <TextArea  placeholder="商品描述" autoSize ={{minRows:2}} />
                            )
                        }

                    </Item>
                    <Item label="商品价格:">
                        {
                            getFieldDecorator('price',{
                                initialValue:'',
                                rules:[
                                    {required:true,message:"请输入商品描述"},
                                    {validator:this.validattorPrice}
                                ]
                            })(
                                <Input type="number" addonAfter="元" placeholder="商品价格"></Input>
                            )
                        }
                    </Item>
                    <Item label="商品分类:">
                        <div>商品分类</div>
                    </Item>
                    <Item label="商品分类:">
                        <Cascader
                            options={this.state.options}
                            loadData={this.loadData}
                            onChange={this.onChange}
                            changeOnSelect
                        />
                    </Item>
                    <Item label="商品分类:">
                        <div>商品详情</div>
                    </Item>
                    <Item>
                        <Button type="primary" onClick={this.submit}>提交</Button>
                    </Item>
                </Form>
            </Card>
        )
    }
}

export default Form.create()(ProductAddUpdate);
