import React from 'react'
import {Model, Card, Button, Table, message, Modal} from "antd"
import {PAGE_SIZE} from "../../utils/constant";
import {formateDate} from "../../utils/dateUtils";
import LinkButton from "../../components/link-button";
import AddForm from "../role/add-form";

export default class User extends React.Component {

    state = {
        loading:false,
        users:[],
        isShow:false
    }

    initColumn = ()=>{
        this.columns = [{
            title:'用户明',
            dataIndex:"usernamne"
        },{
            title:"邮箱",
            dataIndex: "email"
        },{
            title:"电话",
            dataIndex: "phone"
        },{
            title:"注册时间",
            dataIndex: "create_time",
            render:formateDate
        },{
            title:"所属角色",
            dataIndex: "role_id",
        },{
            title:"操作",
           render:(user)=>(
            <span>
                <LinkButton>修改</LinkButton>
                <LinkButton>删除</LinkButton>
            </span>
           )
        }
        ]
    }

    addOrUPdateUser = () =>{}


    componentDidMount() {
        this.initColumn()
    }

    render(){
        const title = (<Button type="primary">创建用户</Button>);
        const {loading,users,isShow} = this.state
        return (

            <Card title={title}>
                <Table
                    bordered
                    rowKey="_id"
                    loadding={loading}
                    dataSource={users}
                    pagination={ {defaultPageSize:5} }
                    columns={this.columns} />
                <Modal
                    title="添加用户"
                    visible={isShow}
                    onOk={this.addOrUPdateUser}
                    onCancel={()=>this.setState({isShow: false})}
                >
                    <AddForm  setForm={(form)=> {this.form = form} } />
                </Modal>


            </Card>
        )
    }
}
